import "./styles.css";
import React, { useState } from "react";
import DefaultImg from "./DefaultImg";
import Search from "./Search";
import Temperature from "./Temperature";
import Forecast from "./Forecast";
import axios from "axios";
//
export default function App() {
  const [ready, setReady] = useState(false);
  const [temperature, setTemperature] = useState(null);

  function handleResponse(response) {
    console.log(response.data);
    setTemperature(response.data.main.temp);
    setReady(true);
  }

  if (ready) {
    return (
      <html lang="en">
        <head>
          <title>Weather App</title>
          <link
            href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css"
            rel="stylesheet"
            integrity="sha384-T3c6CoIi6uLrA9TneNEoa7RxnatzjcDSCmG1MXxSR1GAsXEV/Dwwykc2MPK8M2HN"
            crossorigin="anonymous"
          />
          <link href="src/style.css" rel="stylesheet" type="text/css" />
          <script src="https://cdn.jsdelivr.net/npm/axios@1.1.2/dist/axios.min.js"></script>
        </head>
        <body>
          <div className="container">
            <div className="weather">
              <Search />
              <br />
              <div className="city" id="cityName">
                Pittsburgh PA, United States
              </div>
              <div className="mainText" id="dateTime">
                Thursday 13:00
              </div>
              <DefaultImg />
              <Temperature />
            </div>
          </div>
          <p>
            <a href="https://github.com/Its-Shio/weather-app-react">Code</a> by{" "}
            <a href="https://github.com/Its-Shio">Dominica Kelly</a>
          </p>
          <script src="src/index.js"></script>
        </body>
      </html>
    );
  } else {
    const apiKey = "6f578b96aa9505bcce148ac22cb85794";
    let city = "Rivne";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q={city}&appid={apiKey}`;
    axios.get(apiUrl).then(handleResponse);

    return "Loading...";
  }
}
