import React, { useState } from "react";
import WeatherInfo from "./WeatherInfo";
import "./styles.css";
import axios from "axios";

export default function Weather(props) {
  const [weatherData, setWeatherData] = useState({ ready: false });
  const [city, setCity] = useState(props.defaultCity);

  function handleResponse(response) {
    console.log(response.data);
    let tempDesc = response.data.weather[0].description;
    tempDesc = tempDesc
      .toLowerCase()
      .split(" ")
      .map((s) => s.charAt(0).toUpperCase() + s.substring(1))
      .join(" ");

    setWeatherData({
      ready: true,
      temperature: response.data.main.temp,
      wind: response.data.wind,
      city: response.data.name,
      country: response.data.sys.country,
      image: `https://openweathermap.org/img/wn/${response.data.weather[0].icon}@2x.png`,
      wind: response.data.wind.speed,
      humidity: response.data.main.humidity,
      date: new Date(response.data.dt * 1000),
      desc: tempDesc,
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    search(city);
  }
  function updateCity(event) {
    setCity(event.target.value);
  }

  if (weatherData.ready) {
    return (
      <div>
        <form onSubmit={handleSubmit}>
          <input type="text" id="cityInput" placeholder="Enter a city" />
          <input
            className="submitBtn"
            type="submit"
            value="Search"
            id="btnSubmit"
            onChange={updateCity}
          />
        </form>
        <button className="currentBtn" id="btnCurrent">
          Current
        </button>
        <br />
        <WeatherInfo data={weatherData} />
      </div>
    );
  } else {
    const apiKey = "25fad9f7e87157d33dde0f82ab269ee8";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);

    return "Loading...";
  }
}
