import React, { useState } from "react";
import "./Forecast.css";
import DisplayImg from "./DisplayImg";
import ForecastDate from "./FormattedDate";
import axios from "axios";

export default function WeatherForecast(props) {
  let [loaded, setLoaded] = useState(false);
  let [forecast, setForecast] = useState(null);
  let date;

  function handleResponse(response) {
    setForecast(response.data.daily);
    setLoaded(true);
  }

  if (loaded) {
    date = new Date(forecast[0].dt * 1000);
    return (
      <div className="WeatherForecast">
        <div className="ForecastRow">
          <div className="ForecastCol">
            <ForecastDate date={date} />
            <DisplayImg icon={forecast[0].weather[0].icon} />
            <span className="max">{Math.round(forecast[0].temp.max)}°</span>
            <span className="min">{Math.round(forecast[0].temp.min)}°</span>
          </div>
        </div>
      </div>
    );
  } else {
    const apiKey = "25fad9f7e87157d33dde0f82ab269ee8";
    let lat = props.coordinates.lat;
    let lon = props.coordinates.lon;
    let apiUrl = `https://api.openweathermap.org/data/2.5/onecall?lat=${lat}&lon=${lon}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);

    return null;
  }
}
